mapper.select:
selectfrom TBUILD_API_GROUP
         and API_GROUP_VALUE like #{apiGroupValue} 
         and API__GROUP_TEXT like #{apiGroupText} 
         and API_GROUP_DESC like #{apiGroupDesc} 
         and API_GROUP_STATUS like #{apiGroupStatus} 
         and API_GROUP_PARENT_ID like #{apiGroupParentId} 
         and API_GROUP_IS_SYSTEM like #{apiGroupIsSystem} 
         and API_GROUP_DEL_ENABLE like #{apiGroupDelEnable} 
         and API_GROUP_PID_LIST like #{apiGroupPidList} 
         and API_GROUP_ORGAN_ID like #{apiGroupOrganId} 
         and API_GROUP_ORGAN_NAME like #{apiGroupOrganName} 
    ORDER by API_GROUP_ORDER_NUM DESC
