mapper.select:
selectfrom TBUILD_API_ITEM
         and API_ITEM_VALUE like #{apiItemValue} 
         and API_ITEM_TEXT like #{apiItemText} 
         and API_ITEM_CLASS_NAME like #{apiItemClassName} 
         and API_ITEM_CLASS_PARA like #{apiItemClassPara} 
         and API_ITEM_REST like #{apiItemRest} 
         and API_ITEM_REST_PARA like #{apiItemRestPara} 
         and API_ITEM_GROUP_ID like #{apiItemGroupId} 
         and API_ITEM_IS_SYSTEM like #{apiItemIsSystem} 
         and API_ITEM_DEL_ENABLE like #{apiItemDelEnable} 
         and API_ITEM_STATUS like #{apiItemStatus} 
         and API_ITEM_DESC like #{apiItemDesc} 
         and API_ITEM_EX_ATTR1 like #{apiItemExAttr1} 
         and API_ITEM_EX_ATTR2 like #{apiItemExAttr2} 
         and API_ITEM_EX_ATTR3 like #{apiItemExAttr3} 
         and API_ITEM_EX_ATTR4 like #{apiItemExAttr4} 
         and API_ITEM_USER_ID like #{apiItemUserId} 
         and API_ITEM_USER_NAME like #{apiItemUserName} 
         and API_ITEM_ORGAN_ID like #{apiItemOrganId} 
         and API_ITEM_ORGAN_NAME like #{apiItemOrganName} 
    ORDER by API_ITEM_ORDER_NUM DESC
