mapper.select:
selectfrom TBUILD_ENV_VARIABLE
         and ENV_VAR_VALUE like #{envVarValue} 
         and ENV_VAR_TEXT like #{envVarText} 
         and ENV_VAR_CLASS_NAME like #{envVarClassName} 
         and ENV_VAR_CLASS_PARA like #{envVarClassPara} 
         and ENV_VAR_REST like #{envVarRest} 
         and ENV_VAR_REST_PARA like #{envVarRestPara} 
         and ENV_VAR_GROUP_ID like #{envVarGroupId} 
         and ENV_VAR_IS_SYSTEM like #{envVarIsSystem} 
         and ENV_VAR_DEL_ENABLE like #{envVarDelEnable} 
         and ENV_VAR_STATUS like #{envVarStatus} 
         and ENV_VAR_DESC like #{envVarDesc} 
         and ENV_VAR_EX_ATTR1 like #{envVarExAttr1} 
         and ENV_VAR_EX_ATTR2 like #{envVarExAttr2} 
         and ENV_VAR_EX_ATTR3 like #{envVarExAttr3} 
         and ENV_VAR_EX_ATTR4 like #{envVarExAttr4} 
         and ENV_VAR_USER_ID like #{envVarUserId} 
         and ENV_VAR_USER_NAME like #{envVarUserName} 
         and ENV_VAR_ORGAN_ID like #{envVarOrganId} 
         and ENV_VAR_ORGAN_NAME like #{envVarOrganName} 
    ORDER by ENV_VAR_ORDER_NUM DESC
