mapper.select:
selectfrom TBUILD_FLOW_INTEGRATED
         and INTEGRATED_DE_ID like #{integratedDeId} 
         and INTEGRATED_DE_MESSAGE like #{integratedDeMessage} 
         and INTEGRATED_DE_SUCCESS like #{integratedDeSuccess} 
         and INTEGRATED_MODULE_ID like #{integratedModuleId} 
         and INTEGRATED_CODE like #{integratedCode} 
         and INTEGRATED_NAME like #{integratedName} 
         and INTEGRATED_CREATOR like #{integratedCreator} 
         and INTEGRATED_UPDATER like #{integratedUpdater} 
         and INTEGRATED_DESC like #{integratedDesc} 
         and INTEGRATED_STATUS like #{integratedStatus} 
         and INTEGRATED_DEPLOYMENT_ID like #{integratedDeploymentId} 
         and INTEGRATED_START_KEY like #{integratedStartKey} 
         and INTEGRATED_RESOURCE_NAME like #{integratedResourceName} 
         and INTEGRATED_FROM_TYPE like #{integratedFromType} 
    ORDER by INTEGRATED_ORDER_NUM DESC
