mapper.select:
selectfrom TFLOW_MODEL_ASS_OBJECT
         and OBJECT_MODEL_ID like #{objectModelId} 
         and OBJECT_TYPE like #{objectType} 
         and OBJECT_RE_KEY like #{objectReKey} 
         and OBJECT_TEXT like #{objectText} 
         and OBJECT_VALUE like #{objectValue} 
         and OBJECT_CREATOR like #{objectCreator} 
         and OBJECT_CREATOR_ID like #{objectCreatorId} 
         and OBJECT_DESC like #{objectDesc} 
    ORDER by OBJECT_ORDER_NUM DESC
