mapper.select:
selectfrom TFLOW_MODEL_GROUP
         and MODEL_GROUP_VALUE like #{modelGroupValue} 
         and MODEL_GROUP_TEXT like #{modelGroupText} 
         and MODEL_GROUP_DESC like #{modelGroupDesc} 
         and MODEL_GROUP_STATUS like #{modelGroupStatus} 
         and MODEL_GROUP_PARENT_ID like #{modelGroupParentId} 
         and MODEL_GROUP_IS_SYSTEM like #{modelGroupIsSystem} 
         and MODEL_GROUP_DEL_ENABLE like #{modelGroupDelEnable} 
         and MODEL_GROUP_CLASS_NAME like #{modelGroupClassName} 
    ORDER by MODEL_GROUP_ORDER_NUM DESC
