mapper.select:
selectfrom PORTLET_WIDGET
         and WIDGET_GROUP_ID like #{widgetGroupId} 
         and WIDGET_TITLE like #{widgetTitle} 
         and WIDGET_NAME like #{widgetName} 
         and WIDGET_DESC like #{widgetDesc} 
         and WIDGET_CLIENT_RENDER like #{widgetClientRender} 
         and WIDGET_BEF_RENDER like #{widgetBefRender} 
         and WIDGET_AFT_RENDER like #{widgetAftRender} 
         and WIDGET_UPDATER like #{widgetUpdater} 
         and WIDGET_STATUS like #{widgetStatus} 
         and WIDGET_PROPERTIES like #{widgetProperties} 
    ORDER by WIDGET_ORDER_NUM DESC
