mapper.select:
selectfrom TBUILD_SITE_FOLDER
         and FOLDER_SITE_ID like #{folderSiteId} 
         and FOLDER_NAME like #{folderName} 
         and FOLDER_PARENT_ID like #{folderParentId} 
         and FOLDER_CREATOR like #{folderCreator} 
         and FOLDER_UPDATER like #{folderUpdater} 
         and FOLDER_DESC like #{folderDesc} 
         and FOLDER_STATUS like #{folderStatus} 
         and FOLDER_TYPE like #{folderType} 
    ORDER by FOLDER_ORDER_NUM DESC
