mapper.select:
selectfrom TBUILD_LIST_BUTTON
         and BUTTON_LIST_ID like #{buttonListId} 
         and BUTTON_LIST_ELEM_ID like #{buttonListElemId} 
         and BUTTON_CAPTION like #{buttonCaption} 
         and BUTTON_CONTENT like #{buttonContent} 
         and BUTTON_AUTH like #{buttonAuth} 
         and BUTTON_DESC like #{buttonDesc} 
    ORDER by  DESC
