mapper.select:
selectfrom TGRID_BUILD_INFO
         and BUILD_CATEGORY like #{buildCategory} 
         and BUILD_FILE_CODE like #{buildFileCode} 
         and BUILD_CITY_ID like #{buildCityId} 
         and BUILD_AREA_ID like #{buildAreaId} 
         and BUILD_STREET_ID like #{buildStreetId} 
         and BUILD_COMMUNITY_ID like #{buildCommunityId} 
         and BUILD_GRID_ID like #{buildGridId} 
         and BUILD_MAP_LOCATION like #{buildMapLocation} 
         and BUILD_CODE like #{buildCode} 
         and BUILD_TYPE like #{buildType} 
         and BUILD_STATUS like #{buildStatus} 
         and BUILD_ADDRESS like #{buildAddress} 
         and BUILD_ADDRESS_TITLE_CER like #{buildAddressTitleCer} 
         and BUILD_NAME like #{buildName} 
         and BUILD_FUNCTION_CATEGORY like #{buildFunctionCategory} 
         and BUILD_PROPERTY like #{buildProperty} 
         and BUILD_IS_ENTRANCE_GUARD like #{buildIsEntranceGuard} 
         and BUILD_IS_VIDEO_MONITORING like #{buildIsVideoMonitoring} 
         and BUILD_MANAGEMENT like #{buildManagement} 
         and BUILD_CONTACT like #{buildContact} 
         and BUILD_CONTACT_PHONE like #{buildContactPhone} 
         and BUILD_STRUCTURE like #{buildStructure} 
         and BUILD_DESIGN_FOR like #{buildDesignFor} 
         and BUILD_FLOOR_DES like #{buildFloorDes} 
         and BUILD_IS_ELEVATOR like #{buildIsElevator} 
         and BUILD_CHECK_IS_GRID like #{buildCheckIsGrid} 
         and BUILD_CHECK_IS_CODE like #{buildCheckIsCode} 
         and BUILD_CHECK_IS_ADDRESS like #{buildCheckIsAddress} 
         and BUILD_CHECK_IS_MAP like #{buildCheckIsMap} 
         and BUILD_CHECK_IS_GROUP_CODE like #{buildCheckIsGroupCode} 
         and BUILD_CHECK_IS_FLOOR_NUM like #{buildCheckIsFloorNum} 
         and BUILD_CHECK_IS_USER like #{buildCheckIsUser} 
         and BUILD_INPUT_UNIT_NAME like #{buildInputUnitName} 
         and BUILD_INPUT_UNIT_ID like #{buildInputUnitId} 
         and BUILD_INPUT_USER_NAME like #{buildInputUserName} 
         and BUILD_INPUT_USER_ID like #{buildInputUserId} 
         and BUILD_REMARK like #{buildRemark} 
         and BUILD_SP_ADDRESS like #{buildSpAddress} 
         and BUILD_SP_OWNER_NAME like #{buildSpOwnerName} 
         and BUILD_SP_OWNER_ADDRESS like #{buildSpOwnerAddress} 
         and BUILD_SP_OWNER_PHONE like #{buildSpOwnerPhone} 
         and BUILD_SP_TYPE like #{buildSpType} 
         and BUILD_IS_VIRTUAL like #{buildIsVirtual} 
         and BUILD_PARENT_ID like #{buildParentId} 
         and BUILD_PARENT_ID_LIST like #{buildParentIdList} 
         and BUILD_RECORD_STATUS like #{buildRecordStatus} 
    ORDER by BUILD_ORDER_NUM DESC
