mapper.select:
selectfrom TGRID_HOUSE_INFO
         and HOUSE_BUILD_ID like #{houseBuildId} 
         and HOUSE_CODE like #{houseCode} 
         and HOUSE_NUM_NAME like #{houseNumName} 
         and HOUSE_CODE_FULL like #{houseCodeFull} 
         and HOUSE_USED_DESC like #{houseUsedDesc} 
         and HOUSE_DESIGN_FOR like #{houseDesignFor} 
         and HOUSE_USE_FOR like #{houseUseFor} 
         and HOUSE_STRUCTURE like #{houseStructure} 
         and HOUSE_IS_RENTAL_HOUSING like #{houseIsRentalHousing} 
         and HOUSE_REMARK like #{houseRemark} 
         and HOUSE_INPUT_UNIT_NAME like #{houseInputUnitName} 
         and HOUSE_INPUT_UNIT_ID like #{houseInputUnitId} 
         and HOUSE_INPUT_USER_NAME like #{houseInputUserName} 
         and HOUSE_INPUT_USER_ID like #{houseInputUserId} 
    ORDER by HOUSE_ORDER_NUM DESC
