mapper.select:
selectfrom TGRID_EVENT_INFO
         and EVENT_CODE like #{eventCode} 
         and EVENT_ACCEPT_UNIT_NAME like #{eventAcceptUnitName} 
         and EVENT_ACCEPT_UNIT_ID like #{eventAcceptUnitId} 
         and EVENT_ACCEPT_USER_NAME like #{eventAcceptUserName} 
         and EVENT_ACCEPT_USER_ID like #{eventAcceptUserId} 
         and EVENT_ACCEPT_GRID_ID like #{eventAcceptGridId} 
         and EVENT_ACCEPT_MAP_LOCATION like #{eventAcceptMapLocation} 
         and EVENT_SOURCE like #{eventSource} 
         and EVENT_TYPE_1 like #{eventType1} 
         and EVENT_TYPE_2 like #{eventType2} 
         and EVENT_LEVEL like #{eventLevel} 
         and EVENT_SEVERITY like #{eventSeverity} 
         and EVENT_ADDRESS like #{eventAddress} 
         and EVENT_APPEAL_PURPOSE like #{eventAppealPurpose} 
         and EVENT_APPEAL_QUESTION like #{eventAppealQuestion} 
         and EVENT_IS_PETITION like #{eventIsPetition} 
         and EVENT_IS_RENTAL_HOUSING like #{eventIsRentalHousing} 
         and EVENT_IS_GROUP like #{eventIsGroup} 
         and EVENT_FROM_CODE like #{eventFromCode} 
         and EVENT_IS_EMERGENCY like #{eventIsEmergency} 
         and EVENT_IS_SP_DIFFICULT like #{eventIsSpDifficult} 
         and EVENT_WARRING_LEVEL like #{eventWarringLevel} 
         and EVENT_PROCESS_NODE_NAME like #{eventProcessNodeName} 
         and EVENT_PROCESS_NODE_VALUE like #{eventProcessNodeValue} 
         and EVENT_REMARK like #{eventRemark} 
    ORDER by EVENT_ORDER_NUM DESC
