mapper.select:
selectfrom TGRID_PERSON
         and PERSON_FAMILY_ID like #{personFamilyId} 
         and PERSON_HOUSE_ID like #{personHouseId} 
         and PERSON_NAME like #{personName} 
         and PERSON_RELATIONSHIP like #{personRelationship} 
         and PERSON_ID_CARD like #{personIdCard} 
         and PERSON_SEX like #{personSex} 
         and PERSON_NATION like #{personNation} 
         and PERSON_HR_LOCATION like #{personHrLocation} 
         and PERSON_HR_LEAVE like #{personHrLeave} 
         and PERSON_HR_LEAVE_FOR like #{personHrLeaveFor} 
         and PERSON_EDUCATION like #{personEducation} 
         and PERSON_SP_TYPE like #{personSpType} 
         and PERSON_PHONE_ID like #{personPhoneId} 
         and PERSON_PHONE like #{personPhone} 
         and PERSON_INPUT_UNIT_NAME like #{personInputUnitName} 
         and PERSON_INPUT_UNIT_ID like #{personInputUnitId} 
         and PERSON_INPUT_USER_NAME like #{personInputUserName} 
         and PERSON_INPUT_USER_ID like #{personInputUserId} 
         and PERSON_CITY_ID like #{personCityId} 
         and PERSON_AREA_ID like #{personAreaId} 
         and PERSON_STREET_ID like #{personStreetId} 
         and PERSON_COMMUNITY_ID like #{personCommunityId} 
         and PERSON_GRID_ID like #{personGridId} 
         and PERSON_CATEGORY like #{personCategory} 
         and PERSON_FORE_EN_NAME like #{personForeEnName} 
         and PERSON_FORE_NATIONALITY like #{personForeNationality} 
         and PERSON_FORE_CERTIFICATE_TYPE like #{personForeCertificateType} 
         and PERSON_FORE_CERTIFICATE_NUM like #{personForeCertificateNum} 
         and PERSON_REMARK like #{personRemark} 
    ORDER by PERSON_ORDER_NUM DESC
