mapper.select:
selectfrom TGRID_GATHER_TERMINAL_INFO
         and TERMINAL_USER_NAME like #{terminalUserName} 
         and TERMINAL_USER_ID like #{terminalUserId} 
         and TERMINAL_STATUS like #{terminalStatus} 
         and TERMINAL_CODE like #{terminalCode} 
         and TERMINAL_DESC like #{terminalDesc} 
         and TERMINAL_REMARK like #{terminalRemark} 
         and TERMINAL_MANAGE_UNIT_NAME like #{terminalManageUnitName} 
         and TERMINAL_MANAGE_UNIT_ID like #{terminalManageUnitId} 
         and TERMINAL_MANAGE_USER_NAME like #{terminalManageUserName} 
         and TERMINAL_MANAGE_USER_ID like #{terminalManageUserId} 
    ORDER by TERMINAL_ORDER_NUM DESC
