<script>
import axios from 'axios';
const appClientUtility = require('../Js/AppClientUtility.js');

export default {
  name: "gather-house-detail-edit",
  props: ["selectedBuild", "session"],
  data:function (){
    return {
      acInterface: {
        //person
        //getPersonByHouseId: "/GridSystem/Rest/Grid/Person/PersonMain/GetPersonByHouseId",
        saveHouseData: "/GridSystem/Rest/Grid/Build/HouseInfo/SaveHouseData",
        getRelevanterByHouseId: "/GridSystem/Rest/Grid/Build/HouseInfo/GetRelevanterByHouseId",
        getImageBase64String:"/GridSystem/Rest/Builder/RunTime/FileRuntime/GetImageBase64String",
        deleteRelevanter: "/GridSystem/Rest/Grid/Build/HouseInfo/DeleteRelevanter",
      },
      house:{
        editHouseData:{
          houseId:"",
          houseBuildId:"",//所在建筑物ID
          houseCode:"",//房屋编码
          houseNumName:"",//房号
          houseCodeFull:"",//完整编码
          houseFloorNum:"",//楼层
          houseUsedDesc:"",//使用情况
          houseDesignFor:"住宅",//设计用途:商业,办公,住宅,厂房,仓库,其它
          houseDesignArea:0,//设计面积
          houseUseFor:"住宅",//使用用途:商业,办公,住宅,厂房,仓库,其它
          houseUseArea:0,//使用面积
          houseStructure:"",//住宅房型
          houseIsRentalHousing:"否",//是否出租屋:是,否
          houseRemark:"",//备注
          houseOrderNum:"",//排序号
          houseInputUnitName:"",//填报单位
          houseInputUnitId:"",//填报单位
          houseInputDate:"",//登记时间
          houseInputUserName:"",//登记人
          houseInputUserId:""//登记人ID
        },
        editRelevanterPersons:[

        ],
        emptyHouseData: null
      },
      relevanter:{
        editRelevanterData:{
          reterId:"",
          reterHouseId:"",//房屋ID
          reterType:"产权人",//相关人员类型:产权人,代理人
          reterName:"",//相关人员姓名
          reterPhone:"",//相关人员电话号码
          reterCertCategory:"身份证",//相关人员证件类别
          reterCertCode:"",//证件号码
          reterAddress:"",//地址
          reterPercent:"",//产权比例
          reterRemark:"",//备注
          reterOrderNum:"",//排序号
          reterPhotoId:"",//照片ID
          reterIdCardUuid:"",//身份证UUID
          reterIdCardPublicForm:"",//身份证签发机关
          reterIdCardEffDate:"",//身份证有效日期
          reterIdCardAddress:"",//居住地址
          reterBirthday:"",//出生日期
          reterNation:"",//民族
          reterHeaderImageBase64:""
        },
        emptyRelevanterData:null
      },
      ddg_HouseIsRentalHousing:[],
      ddg_HouseDesignFor:[],
      ddg_HouseUseFor:[],
      ddg_ReterType:[],
      ddg_ReterCertCategory:[],
      enableDelete:false,
      enableEditCode:true
    }
  },
  mounted() {
    this.house.emptyHouseData = appClientUtility.JsonUtility.CloneStringify(this.house.editHouseData);
    this.relevanter.emptyRelevanterData = appClientUtility.JsonUtility.CloneStringify(this.relevanter.editRelevanterData);

    window["writeHouseRelevanterDataToView"] = (personData,imageBase64) => {
      this.writeHouseRelevanterDataToView(personData,imageBase64)
    }
  },
  methods:{
    buildDDGroupData:function (){
      this.ddg_HouseIsRentalHousing=appClientUtility.GetAllDDMap()["03c27224-0e4e-4ed1-96ac-31aba1576118"];
      this.ddg_HouseDesignFor=appClientUtility.GetAllDDMap()["da589c39-f7b7-4315-a269-6b10a728c989"];
      this.ddg_HouseUseFor=appClientUtility.GetAllDDMap()["caa22c64-a477-47e6-ac42-b5d190e8e700"];
      this.ddg_ReterType=appClientUtility.GetAllDDMap()["eeeb76ee-0772-47b3-993d-6d8accaa3185"];
      this.ddg_ReterCertCategory=appClientUtility.GetAllDDMap()["ff310bd8-4040-4959-8a3b-9f8472ce527f"];
    },
    //region house
    newHouse:function () {
      //debugger;
      this.house.editHouseData = appClientUtility.JsonUtility.CloneStringify(this.house.emptyHouseData);
      this.house.editHouseData.houseId = appClientUtility.StringUtility.NewH5AppRecordId();
      this.enableDelete=false;
      this.enableEditCode=true;
      $("#normalHouseEditModal").modal('show');
    },
    editHouse:function (houseData){
      console.log(houseData);
      this.house.editHouseData=houseData;
      axios.get(this.acInterface.getRelevanterByHouseId, {
        params: {
          houseId: houseData.houseId,
          AppClientToken: this.session.AppClientToken,
          ts:Date.now()
        }
      }).then((response) => {
        console.log(response);
        $("#normalHouseEditModal").modal('show');
        if (response.data.success) {
          this.house.editRelevanterPersons = response.data.data;
          //this.house.filterHouses=this.house.allHouse.filter((item)=>{return true});
        }
      });
      this.enableDelete=true;
      this.enableEditCode=false;
    },
    saveHouseTo:function (){
      var errorMessage = "";
      if (appClientUtility.StringUtility.IsNullOrEmptyTrim(this.house.editHouseData.houseCode)) {
        errorMessage += "[房屋编码不能为空!]<br />";
      }
      if (this.house.editHouseData.houseCode.length!=5) {
        errorMessage += "[房屋编码长度必须为5位!]<br />";
      }
      if (appClientUtility.StringUtility.IsNullOrEmptyTrim(this.house.editHouseData.houseNumName)) {
        errorMessage += "[房号不能为空!]<br />";
      }
      if (appClientUtility.StringUtility.IsNullOrEmptyTrim(this.house.editHouseData.houseUsedDesc)) {
        errorMessage += "[使用情况不能为空!]<br />";
      }
      if (isNaN(this.house.editHouseData.houseDesignArea)) {
        errorMessage += "[设计面积必须是数字!]<br />";
      }
      if (isNaN(this.house.editHouseData.houseUseArea)) {
        errorMessage += "[使用面积必须是数字!]<br />";
      }

      if (errorMessage) {
        appClientUtility.DialogUtility.AlertText(this,errorMessage);
        return;
      }

      this.house.editHouseData.houseBuildId = this.selectedBuild.buildId;

      //this.showLoading = true;
      $("#loadDialogWrap").show();
      axios.post(this.acInterface.saveHouseData, this.house).then((result) => {
        appClientUtility.DialogUtility.AlertText(this,result.data.message,()=>{
          if(result.data.success){
            $('#normalHouseEditModal').modal('hide');
            this.$emit('saveHouseCompleted')
          }
        });
        /*console.log(result);
        if (result.data.success) {
          this.$confirm({
            message: `保存成功`,
            button: {
              yes: '关闭'
            },
            callback: confirm => {
              this.loadHouseInnerDataFromServer(this.selectedHouse);
              $('#familyEditModal').modal('hide');
            }
          });
        } else {
          var errorMessage = result.data.message;
          this.$confirm({
            message: `${errorMessage}`,
            button: {
              yes: '确认'
            },
            callback: confirm => {

            }
          });
        }*/
      }).catch((err) => {

      }).then((endResult) => {
        $("#loadDialogWrap").hide();
      });
    },
    deleteHouse:function (){
      appClientUtility.DialogUtility.Confirm(this,"确实删除该记录吗?",()=>{
        this.$emit('tryDeleteHouse',this.house.editHouseData);
      });
    },
    //endregion
    //region relevanter
    getRelevanterFromArray: function (relevanterId) {
      //debugger;
      for (let i = 0; i < this.house.editRelevanterPersons.length; i++) {
        if (this.house.editRelevanterPersons[i].reterId == relevanterId) {
          return this.house.editRelevanterPersons[i];
        }
      }
      return null;
    },
    addNewRelevanter:function (){
      $("#relevanterEditModal").modal('show');
      this.relevanter.editRelevanterData = appClientUtility.JsonUtility.CloneStringify(this.relevanter.emptyRelevanterData);
      this.relevanter.editRelevanterData.reterId = appClientUtility.StringUtility.NewH5AppRecordId();

      /*if(typeof(appBridge)!="undefined"){
        appBridge.beginReadIdCardFromNFC("writePersonDataToViewEX1");
      }*/
    },
    getRelevanterHeaderImageBase64: function () {
      if (!this.relevanter.editRelevanterData.reterHeaderImageBase64) {
        return "";
      }
      return this.relevanter.editRelevanterData.reterHeaderImageBase64;
    },
    saveRelevanterTo:function (){

      var relevanterPerson = this.getRelevanterFromArray(this.relevanter.editRelevanterData.reterId);
      if (!relevanterPerson) {
        this.house.editRelevanterPersons.push(this.relevanter.editRelevanterData);
      } else {
        appClientUtility.JsonUtility.SimpleCloneAttr(relevanterPerson, this.relevanter.editRelevanterData);
      }

      $("#relevanterEditModal").modal('hide');
    },
    deleteRelevanter:function (relevanterData){
      appClientUtility.DialogUtility.Confirm(this,"你确认要删除记录吗?",(confirm)=> {
        if(confirm) {
          $("#loadDialogWrap").show();
          axios.delete(this.acInterface.deleteRelevanter, {
            params: {
              "relevanterId": relevanterData.reterId
            }
          }).then((result) => {
            appClientUtility.DialogUtility.AlertText(this, result.data.message);
            if (result.data.success) {
              this.house.editRelevanterPersons = result.data.data;
            }
          }).then((endResult) => {
            $("#loadDialogWrap").hide();
          });
        }
      });
    },
    editRelevanter:function (relevanterData){
      $("#relevanterEditModal").modal('show');
      this.relevanter.editRelevanterData = relevanterData;
      this.tryLoadRelevanterHeaderBase64FromServer(relevanterData);
    },
    tryLoadRelevanterHeaderBase64FromServer: function (relevanterData) {
      if (relevanterData.reterPhotoId) {
        axios.get(this.acInterface.getImageBase64String, {
          params: {
            fileId: relevanterData.reterPhotoId,
            AppClientToken: this.session.AppClientToken,
            ts: Date.now()
          }
        }).then((result) => {
          if (result.data.success) {
            this.relevanter.editRelevanterData.reterHeaderImageBase64 = result.data.data;
            $("#imgRelevanterHeadPhoto").attr("src", 'data:image/png;base64,' +this.relevanter.editRelevanterData.reterHeaderImageBase64);
            //console.log(person.personHeaderImageBase64);
          }
        })
      }
    },
    readRelevanterDataFromNFC: function () {
      //this.showLoading = true;
      if(typeof(appBridge)!="undefined"){
        $("#loadDialogWrap").show();
        appBridge.beginReadIdCardFromNFC("writeHouseRelevanterDataToView");
      }
      else{
        this.$toasted.show('不存在appBridge对象!',{duration:2000});
      }

      return
      window.setTimeout(() => {
        var personData = {
          personName: "江东小妮玛",
          personIdCard: "445120121541512141",
          personIdCardUuid: "123123123123123123",
          personIdCardPublicForm: "江东小妮玛来自哪里",
          personIdCardEffDate: "20012222-200022222",
          personIdCardAddress: "江东小妮玛不知道来哪里",
          personBirthday: "2020年1月1日"
        };
        var imageBase64 = "";

        var num = Math.floor(Math.random() * 10 + 1);
        if (num > 7) {
          personData = {
            personName: "李真",
            personIdCard: "445120121541512141",
            personIdCardUuid: "123123123123123123",
            personIdCardPublicForm: "李真来自哪里",
            personIdCardEffDate: "20012222-200022222",
            personIdCardAddress: "李真不知道来哪里",
            personBirthday: "2020年1月1日"
          };
          imageBase64 = "";
        } else if (num > 5) {
          personData = {
            personName: "江南大光头",
            personIdCard: "665120121541512141",
            personIdCardUuid: "123123123123123123",
            personIdCardPublicForm: "江南大光头来自哪里",
            personIdCardEffDate: "20012222-200022222",
            personIdCardAddress: "江南大光头不知道来哪里",
            personBirthday: "2020年1月1日"
          };
          imageBase64 = "";

        } else if (num > 3) {
          personData = {
            personName: "江东大妮玛",
            personIdCard: "775120121541512141",
            personIdCardUuid: "123123123123123123",
            personIdCardPublicForm: "江东大妮玛来自哪里",
            personIdCardEffDate: "20012222-200022222",
            personIdCardAddress: "江东大妮玛不知道来哪里",
            personBirthday: "2020年1月1日"
          };
          imageBase64 = "";
        } else {
          personData = {
            personName: "江西小老头",
            personIdCard: "885120121541512141",
            personIdCardUuid: "123123123123123123",
            personIdCardPublicForm: "江西小老头来自哪里",
            personIdCardEffDate: "20012222-200022222",
            personIdCardAddress: "江西小老头不知道来哪里",
            personBirthday: "2020年1月1日"
          };
          imageBase64 = "";
        }

        this.writeRelevanterDataToView(appClientUtility.JsonUtility.JsonToString(personData), imageBase64);
      }, 3000);
    },
    writeHouseRelevanterDataToView: function (personData, imageBase64) {
      personData = appClientUtility.JsonUtility.StringToJson(personData);
      this.relevanter.editRelevanterData.reterName = personData.personName;
      this.relevanter.editRelevanterData.reterCertCode = personData.personIdCard;
      this.relevanter.editRelevanterData.reterIdCardUuid = personData.personIdCardUuid;
      this.relevanter.editRelevanterData.reterIdCardPublicForm = personData.personIdCardPublicForm;
      this.relevanter.editRelevanterData.reterIdCardEffDate = personData.personIdCardEffDate;
      this.relevanter.editRelevanterData.reterIdCardAddress = personData.personIdCardAddress;
      this.relevanter.editRelevanterData.reterNation=personData.personNation;
      if (personData.personBirthday) {
        var birthdayStr = personData.personBirthday.replace("年", "-").replace("月", "-").replace("日", "");
        var year = birthdayStr.split("-")[0];
        var month = birthdayStr.split("-")[1];
        var day = birthdayStr.split("-")[2];
        var myDate = new Date(year, month, day);
        this.relevanter.editRelevanterData.reterBirthday = appClientUtility.DateUtility.Format(myDate, "yyyy-MM-dd");
      }
      this.relevanter.editRelevanterData.reterHeaderImageBase64 = imageBase64;
      //this.showLoading = false;
      $("#loadDialogWrap").hide();
    }
    //endregion
  }
}
</script>

